import { createGlobal } from "./globals/create-global";
import { GlobalSetter, GlobalGetter } from "./interfaces";

/**
 * Register a global variable shared across workers and main thread.
 *
 * @param id id associated to the class
 * @param registrable class to register
 */
export function registerGlobal<T>(
  name: string,
  defaultValue?: T
): [GlobalGetter<T>, GlobalSetter<T>] {
  return createGlobal(name, defaultValue);
}
