/**
 * Copy a variable content in another one
 * @internal
 * @param from Variable to copy
 * @param to Variable which is copy into
 */
export function copy(from: any, to: any) {
  if (Array.isArray(to)) {
    to.splice(0, to.length);
  } else if (typeof to === "object") {
    for (const i in to) {
      delete to[i];
    }
  }
  for (const i in from) {
    if (Array.isArray(from[i])) {
      to[i] = [];
      copy(from[i], to[i]);
    } else if (typeof from[i] === "object") {
      to[i] = {};
      copy(from[i], to[i]);
    } else {
      to[i] = from[i];
    }
  }
}
